import { CartActionTypes } from './cart.types'

const INITIAL_STATE = {
    visible: false
}

const cartReducer = (state = INITIAL_STATE, action) => {
    switch(action.type) {
        case CartActionTypes.DROP_DOWN_VISIBILITY:
            return {
                ...state,
                visible: action.payload
            }
        default: 
            return state
    }
}

export default cartReducer
